import React, { useState, useEffect } from 'react';
import Service from '../service';
import Header from '../component/header';
import CharacterList from '../component/charactersListing';

export default function ListPage({ history }) {

    const urlAPI = 'https://api.pokemontcg.io/v1/cards';
    const [loading, setLoading] = useState(false);
    const [pokemonList, setPokemonList] = useState([]);
    const [searching, setSearching] = useState(false);
    const [error, setError] = useState(false);

    const getList = (url) => {

        setLoading(true);

        const response = Service.getList();

        setPokemonList(response.cards);
        setLoading(false);
    };

    const getSearch = (value) => {

        setSearching(true);

        getList(`${urlAPI}?name=${value}`);
    };

    useEffect(getList, []);

    return (
        <div>
            <Header onClick={getList} onSearch={getSearch} />

            <div>
                {loading && !error ?
                    <h1>Loading... </h1> :

                    <CharacterList pokemons={pokemonList} onClick={id => history.push(`/details/${id}`)} />
                }

                {error && <h1 style={{ color: 'red' }}>Something went wrong, try again</h1>}

            </div>

        </div>
    );
};
