import React from 'react';

const url = 'https://api.pokemontcg.io/v1/cards';

export function listing(value) {
    

    const response = fetch(value ? `${url}?name=${value}` : url)
        .then(response => {
            if (!response.ok) {
                throw new Error();
            }
            return response.json()
        });

    return response;
};

export function getDetails(value) {
    
    const newUrl = `${url}/${value}`;

    const response = fetch(`${url}/${value}`)
        .then(response => {
            if (!response.ok) {
                throw new Error();
            }
            return response.json()
        });

    return response;
};
