import React from 'react';
import CharacterDetails from './characterDetails';
import DetailsCard from './detailsCard';
import BackButton from './backButton';
import Attacks from './attacks';
import Weakness from './weakness';

const styleDetailBody = {
    display: 'flex',
    backgroundColor: 'rgb(255, 241, 251)',
    boxShadow: '0 4px 8px 0 rgba(255, 5, 159, 0.2)',
    transition: '0.3s',
    borderRadius: '3%'
};

export default function PokemonDetail({ PokemonDetail, pokemonWeakness, pokemonAttacks, onClick }) {

    return (
    <div>

        <div style={styleDetailBody}>
            <DetailsCard pokemon={PokemonDetail} />
            <CharacterDetails pokemon={PokemonDetail} >

                <div>
                    {pokemonAttacks.map((attack, i) => {
                        return <Attacks
                            key={i}
                            name={attack.name}
                            text={attack.text}
                            cost={attack.cost} />
                    })}
                </div>

                <div>
                    {pokemonWeakness.map((weakness, i) => {
                        return <Weakness
                            key={i}
                            weakness={weakness.type}
                            value={weakness.value} />
                    })}
                </div>

            </CharacterDetails>
        </div>

        <BackButton onClick={onClick}></BackButton>
    </div>
)
};
