import React, { useState, useEffect } from 'react';
import Header from '../component/header';
import PokemonDetail from '../component/pokemonDetails';
import ItemDetails from '../component/itemDetails';

export default function DetailsPage({ history, match }) {

    const urlAPI = `https://api.pokemontcg.io/v1/cards/${match.params.id}`;
    const [loading, setLoading] = useState(false);
    const [pokemonDetails, setPokemonDetails] = useState([]);
    const [pokemonAttacks, setPokemonAttacks] = useState([]);
    const [pokemonWeakeness, setPokemonWeakeness] = useState([]);
    const [conditional, setConditional] = useState('');
    const [searching, setSearching] = useState(false);
    const [error, setError] = useState(false);

    const getDetail = () => {

        setLoading(true);

        console.log(urlAPI);

        fetch(urlAPI)
            .then(response => {
                if (!response.ok) {
                    throw new Error();
                }
                return response;
            }).then(response => response.json())
            .then(response => {
                setPokemonDetails(response.card);
                setConditional(response.card.supertype);
                setPokemonAttacks(response.card.attacks);
                setPokemonWeakeness(response.card.weaknesses);
                console.log(pokemonDetail.supertype);
                setLoading(false);
            },
                () => { setError(true) });

    };

    const getSearch = (value) => {

        setSearching(true);

        const newUrls = `https://api.pokemontcg.io/v1/cards/?name=${value}`;

        getDetail(newUrls);
    };


    useEffect(getDetail, []);

    return (
        <div>
            <Header onClick={() => { history.push(`/list`) }} onSearch={getSearch} />

            <div>
                {loading && !error ?

                    <h1>Loading... </h1> :

                    <div>
                        { conditional != 'Pokémon' ?
                            <PokemonDetail pokemonDetails={pokemonDetails} pokemonAttacks={pokemonAttacks} pokemonWeakness={pokemonWeakeness} onClick={history.goBack} />
                            : <ItemDetails pokemonDetails={pokemonDetails} />
                }
                    </div>
                }

                {error && <h1 style={{ color: 'red' }}>Something went wrong, try again</h1>}

            </div>
        </div>
    );
};
