import React from 'react';
import Attacks from '../component/attacks';
import Weakness from '../component/weakness';

const styleDetails = {
    flex: '70%'
};

export default function CharacterDetails({ pokemon }) {
    return (
        <div style={styleDetails} >
            <h2>Pokemon Type</h2>
            <h3>{pokemon.type}</h3>

            <h2>Super Type</h2>
            <h3>{pokemon.supertype}</h3>

            <h2>Evolves From</h2>
            <h3>{props.pokemon.evolvesFrom}</h3>

            <div>
                {pokemon.attacks.forEach(element => {
                    
                    return <Attacks
                        key={element.name}
                        name={element.name}
                        text={element.text}
                        cost={element.cost} />
                })}
            </div>

            <div>
                {pokemon.weaknesses.map(weakness => {
                    return <Weakness
                        key={weakness.type}
                        weakness={weakness.type}
                        value={weakness.value} />
                })}
            </div>
        </div>
    );
};
