import React, { useState, useEffect } from 'react';
import Header from '../component/header';
import PokemonDetail from '../component/pokemonDetail';

export default function DetailsPage({ history }) {

    const urlAPI = 'https://api.pokemontcg.io/v1/cards/xy7-10';
    const [loading, setLoading] = useState(false);
    const [pokemonDetails, setPokemonDetails] = useState([]);
    const [searching, setSearching] = useState(false);
    const [error, setError] = useState(false);

    const getDetail = () => {

        setLoading(true);

        fetch("https://api.pokemontcg.io/v1/cards/xy7-10")
            .then(response => {
                if (!response.ok) {
                    throw new Error();
                }
                return response;
            }).then(response => response.json())
            .then(response => {
                console.log(pokemonDetails);
                setPokemonDetails(response);
                console.log(pokemonDetails);
                setLoading(false);
            },
                () => { setError(true) });

    };

    const getSearch = (value) => {

        setSearching(true);

        const newUrls = `https://api.pokemontcg.io/v1/cards/?name=${value}`;

        getDetail(newUrls);
    };

    useEffect(getDetail, []);

    return (
        <div>
            <Header onClick={getDetail} onSearch={getSearch} />

            <div>
                {loading && !error ?

                    <h1>Loading... </h1> :

                    <PokemonDetail pokemon={pokemonDetails}></PokemonDetail>
                }

                {error && <h1 style={{ color: 'red' }}>Something went wrong, try again</h1>}

            </div>

        </div>
    );
};
