import React, { useState, useEffect } from 'react';
import Header from '../component/header';
import SearchBar from '../component/searchBar';
import CharacterList from '../component/charactersListing';

const ListPage = ({ history }) => {

    const urlAPI = 'https://api.pokemontcg.io/v1/cards';
    const [loading, setLoading] = useState(true);
    const [pokemonList, setPokemonList] = useState([]);
    const [searching, setSearching] = useState(false);
    const [error, setError] = useState(false);

    const getList = () => {

        setLoading(true);

        fetch(urlAPI).then(response => {

            if (!response.ok) {
                throw new Error();
            }
            return response;
        }).then(response => response.json())
            .then(response => {setPokemonList(response.cards)},  () => {setError(true)});
        
        setLoading(false);
    };

    const getSearch = () => {

        setSearching(true);

    }

    useEffect(getList, []);

    return (

        <div>
            <Header>
            </Header>

            <div>
            { (loading) ?
                <h1>Loading... </h1> :

                <CharacterList pokemons={pokemonList} onClick={id => history.push(`/details/${id}`)}></CharacterList>
            }

            <div>
            if (error) {
                <h1>Something went wrong, try again</h1>
            }

            </div>
            </div>
        </div>
    );
};

export default ListPage;
