import React from 'react';
import Attacks from '../component/attacks';
import Weakness from '../component/weakness';

const styleDetails = {
    flex: '70%'
};

export default function CharacterDetails({ pokemon }) {
    return (
        <div style={styleDetails} >
            <h2>Pokemon Type</h2>
            <h3>{pokemon.type}</h3>

            <h2>Super Type</h2>
            <h3>{pokemon.supertype}</h3>

            <h2>Evolves From</h2>
            <h3>{pokemon.evolvesFrom}</h3>

            <div>
                {pokemon.attacks.forEach(function (result) {
                    result.map(attack => {

                        
                        
                        return <Attacks
                        name={attack.name}
                        text={attack.text}
                        cost={attack.cost} />
                    })
                })}
            </div>

            <div>
                {pokemon.weaknesses.forEach(function (result) {
                    return <Weakness
      
                        weakness={result.type}
                        value={result.value} />
                })}
            </div>
        </div>
    );
};
