import React from 'react';

const url = 'https://api.pokemontcg.io/v1/cards';

export function listing(value) {
    

    const response = fetch(value ? `${url}?name=${value}` : url)
        .then(response => {
            if (!response.ok) {
                throw new Error();
            }
            return response.json()
        });

    return response;
};

export function getDetails(id) {
    
    console.log(id);
    const newUrl = `${url}/${id}`;
    
    const response = fetch(newUrl)
        .then(response => {
            if (!response.ok) {
                throw new Error();
            }
            console.log(newUrl);
            return response.json()
        });

    return response;
};
