import React, { useState, useEffect } from 'react';
import Header from '../component/header';
import SearchBar from '../component/searchBar';
import CharacterList from '../component/charactersListing';

const ListPage = ({ history }) => {

    const url = 'https://api.pokemontcg.io/v1/cards';
    const [loading, setLoading] = useState(false);
    const [pokemonList, setPokemonList] = useState([]);

    const getList = () => {
        
            setLoading(true);

            fetch(url).then(response => {

                if (!response.ok) {
                    throw new Error();
                }
                return response;
            }).then()
            const data = response.cards.map(({ id, imageUrl, name }) => ({
                id,
                imageUrl,
                name
            }));
            setPokemonList(data);
        
    };

    useEffect(getList(), []);

    return (
        <div>

            <Header>
                <SearchBar placeholder="Search by name..." onSearch={getList} ></SearchBar>
            </Header>

            { loading ?
                ("Loading...") :
                (<CharacterList pokemons={pokemonList} onClick={id => history.push(`/details/${id}`)}></CharacterList>)
            }

        </div>
    );
};

export default ListPage;
