import React, { useState, useEffect } from 'react';
import Header from '../component/header';
import SearchBar from '../component/searchBar';
import CharacterList from '../component/charactersListing';

const ListPage = ({ history }) => {

    const urlAPI = 'https://api.pokemontcg.io/v1/cards';
    const [loading, setLoading] = useState(false);
    const [pokemonList, setPokemonList] = useState([]);
    const [searching, setSearching] = useState(false);

    const getList = (url) => {

        setLoading(true);

        fetch(url).then(response => {

            if (!response.ok) {
                throw new Error();
            }
            return response;
        }).then(response => response.json())
            .then(response => setPokemonList(response.cards));
    };

    const getSearch = () => {

        setSearching(true);

       


    }

    useEffect(getList(urlAPI), []);

    return (
        <div>

            <Header>
                <SearchBar placeholder="Search by name..." onSearch={getSearch} ></SearchBar>
            </Header>

            { loading ?
                ("Loading...") :
                (<CharacterList pokemons={pokemonList} onClick={id => history.push(`/details/${id}`)}></CharacterList>)
            }

        </div>
    );
};

export default ListPage;
